  import Todo from './Todo';
  import TodoForm from './Todoform';

function App() {
  const [todos,setTodos] = useState([])

  const addTask = (userInput) => {
    if(userInput){
      const newItem = {
        id: Math.random().toString(36).substring(2,9),
        task : userInput,
        complete : false
      }
      setTodos([...todos, newItem])
    }

  }
